export default {
  name: 'BaseInput',
  props: {
    value: {
      type: String,
      required: true,
    },
    id: {
      type: String,
      required: true,
    },
    inputLabel: {
      type: String,
      required: true,
    },
    placeholderText: {
      type: String,
      required: true,
    },
    isRequired: {
      type: Boolean,
      default: false,
      required: false,
    },
    inputModifier: {
      type: String,
      default: '',
      required: false,
    },
    inputType: {
      type: String,
      default: 'text',
    },
    isInvalid: {
      type: Boolean,
      default: false,
    },
    validationText: {
      type: String,
      default: '',
      required: false,
    },
  },
  data() {
    return {
      isFilled: false,
      isFocused: false,
      focusedClass: 'is-focused',
      filledClass: 'is-filled',
      errorClass: 'is-invalid',
    };
  },
  methods: {
    toggleFilledClass() {
      // eslint-disable-next-line no-restricted-globals
      if (event.target.value.length > 0) {
        this.isFilled = true;
      } else {
        this.isFilled = false;
      }
    },
  },
};
